const getTheTitles = function(books) {
    return books.map(book => book.title);

    // basically: return book.title

    // 1. getting the books array
    // 2. looping through each book object
    // 3. getting the title from each book by looping through all of them
    // 4. returns all of the titles
};

// Do not edit below this line
module.exports = getTheTitles;
